import { useReducer, useState } from "react";
import "./App.css";
import Button from "./Button";
import Screen from "./Screen";

const intialState = 0;

const reducer = (state, action) => {
  switch (action) {
    case "increment":
      return state + 1;
    case "decrement":
      return state - 1;
    default:
      return state;
  }
};

function App() {
  const [pvalue, setPValue] = useState();
  const [nvalue, setNValue] = useState();
  const [count, dispatch] = useReducer(reducer, intialState);

  console.log("Pvalue: ", pvalue);
  console.log("Nvalue: ", nvalue);

  const rows = [];
  for (let i = 0; i < pvalue; i++) {
    // note: we are adding a key prop here to allow react to uniquely identify each
    // element in this array. see: https://reactjs.org/docs/lists-and-keys.html
    rows.push(<Button dispatch={dispatch("increment")} />);
  }

  return (
    <div>
      <Screen value={count} />
      Value-1:
      <input
        type="text"
        placeholder="Enter Positive Button Number"
        value={pvalue}
        onChange={(event) => setPValue(event.target.value)}
      />
      <br />
      Value-2 :
      <input
        type="text"
        placeholder="Enter Negative Button Number"
        value={nvalue}
        onChange={(event) => setNValue(event.target.value)}
      />
    </div>
  );
}

export default App;
