import { useState } from "react";
import "./App.css";
import Screen from "./Screen";

function App() {
  const [pvalue, setPValue] = useState();
  const [nvalue, setNValue] = useState();

  return (
    <div>
      <Screen />
      Value-1:
      <input
        type="text"
        placeholder="Enter Positive Button Number"
        value={pvalue}
        onChange={(event) => setPValue(event.target.value)}
      />
      <br />
      <br />
      Value-2 :
      <input
        type="text"
        placeholder="Enter Negative Button Number"
        value={nvalue}
        onChange={(event) => setNValue(event.target.value)}
      />
      console.log("Positive Value", pvalue); console.log("");
    </div>
  );
}

export default App;
