import { useReducer, useState } from "react";
import "./App.css";
import Screen from "./Screen";

const intialState = 0;

const reducer = (state, action) => {
  switch (action) {
    case "increment":
      return state + 1;
    case "decrement":
      return state - 1;
    default:
      return state;
  }
};

function App() {
  const [pvalue, setPValue] = useState();
  const [nvalue, setNValue] = useState();
  const [count, dispatch] = useReducer(reducer, intialState);

  console.log("Pvalue: ", pvalue);
  console.log("Nvalue: ", nvalue);

  // const positive_button = [];
  // for (let i = 0; i < pvalue; i++) {
  //   positive_button.push(<Button dispatch={dispatch("increment")} />);
  // }

  // console.log("Positive Button : ", positive_button);

  // const negative_button = [];
  // for (let i = 0; i < nvalue; i++) {
  //   negative_button.push(<Button dispatch={dispatch("decrement")} />);
  // }

  // console.log("Negative_Button : ", negative_button);

  return (
    <div>
      <Screen value={count} />
      Value-1:
      <input
        type="text"
        placeholder="Enter Positive Button Number"
        value={pvalue}
        onChange={(event) => setPValue(event.target.value)}
      />
      <br />
      Value-2 :
      <input
        type="text"
        placeholder="Enter Negative Button Number"
        value={nvalue}
        onChange={(event) => setNValue(event.target.value)}
      />
      <br />
      <br />
      <button type="button" onClick={() => dispatch("increment")}>
        Increment By 1
      </button>
      <br />
      <br />
      <button type="button" onClick={() => dispatch("decrement")}>
        Decrement By 1
      </button>
    </div>
  );
}

export default App;
